import classes from './CartItem.module.css';
import {useDispatch} from "react-redux";
import {cartSliceActions} from '../../store/cartReducer'

const CartItem = (props) => {
  const { title, quantity, total, price,id } = props.item;
  const dispatch = useDispatch()
const addToCartItem = () => {
    dispatch(cartSliceActions.addItemToCard({
        id,
        name:title,
        quantity,
        price,
        total
    }))
}
const removeFromCartItem = () => {
    dispatch(cartSliceActions.removeItemFromCard(id))
}
  return (
    <li className={classes.item}>
      <header>
        <h3>{title}</h3>
        <div className={classes.price}>
          ${total.toFixed(2)}{' '}
          <span className={classes.itemprice}>(${price.toFixed(2)}/item)</span>
        </div>
      </header>
      <div className={classes.details}>
        <div className={classes.quantity}>
          x <span>{quantity}</span>
        </div>
        <div className={classes.actions}>
          <button
              onClick={removeFromCartItem}>-</button>
          <button onClick={addToCartItem} >+</button>
        </div>
      </div>
    </li>
  );
};

export default CartItem;
