import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import globalSelector from "./Selector/globalSelector";
import {connect} from "react-redux";
import {useEffect,Fragment} from "react";
import {useDispatch} from "react-redux";
import {uiActions} from './store/UiReducer'
import Notification from "./components/UI/Notification/Notification";

function App(props) {
    const dispatch = useDispatch()
    const {cartItems,notification} = props
    useEffect(() => {
        const sendCartData = async () => {
        dispatch(uiActions.showNotification({
            message:'Sending cart data..',
            status:'pending',
            title:'Pending'
        }))
            try {
                const response = await fetch('https://advance-react-424ec-default-rtdb.firebaseio.com/cart.json', {
                    method: 'PUT',
                    body: JSON.stringify(cartItems)
                })
                const resp = await response
                console.log('response',resp)
                if(!response.ok) {
                    throw new Error('Something went wrong')
                }


                dispatch(uiActions.showNotification({
                    message:'Sent cart data successfully',
                    status:'success',
                    title:'Success'
                }))

            } catch (e) {
                console.log('eeee',e.message)
            }

        }
        sendCartData().catch((Error) =>
                console.log('error',Error.message)
        // {
        //     dispatch(uiActions.showNotification({
        //         message:Error,
        //         status:'error',
        //         title:'Error'
        //     }))
        // }
        )

    },[cartItems])
  return (
      <Fragment>
          {notification && <Notification
          title={notification.title}
          messgae={notification.message}
          status={notification.status}
          /> }
          <Layout>
              {props.shoppingCartVisible ? <Cart/> : ''}
              <Products />
          </Layout>
      </Fragment>

  );
}

export default connect(globalSelector)(App);
