import {useHistory,useLocation} from 'react-router-dom'
import { Fragment } from 'react';
import QuoteItem from './QuoteItem';
import classes from './QuoteList.module.css';

const QuoteList = (props) => {
    const history = useHistory()
    const location = useLocation()
    console.log('location',location)
    const queryParam = new URLSearchParams(location.search)
    console.log('queryParam',queryParam)

    const isSortAscending = queryParam.get('sorting')==='asc'
    const changeSortingHandler = () => {
        // history.push('/quotes?sorting=')
        history.push(`/quotes?sorting=${isSortAscending ? 'desc' : 'asc'}`)
    }
  return (
    <Fragment>
        <div className={classes.sorting}>
            <button onClick={changeSortingHandler}> Sort {isSortAscending ? 'Descending' : 'Ascending'}  </button>
        </div>
      <ul className={classes.list}>
        {props.quotes.map((quote) => (
          <QuoteItem
            key={quote.id}
            id={quote.id}
            author={quote.author}
            text={quote.text}
          />
        ))}
      </ul>
    </Fragment>
  );
};

export default QuoteList;
