import React,{useState,useRef} from "react";
const SimpleInput = (props) => {
    const [enteredValue,setEnteredValue] = useState('')
    const [enterNameTouched, setEnterNameTouched] = useState(false)
    const [enteredEmail,setEnteredEmail] = useState('')


const enteredNameIsValid = enteredValue.trim() !== ''
const nameInputFieldIsInvalid = !enteredNameIsValid && enterNameTouched;
const enteredEmailIsValid = enteredEmail.includes('@')
    console.log('enteredEmailIsValid',enteredEmailIsValid)


    let formDisabled = false
    if(enteredNameIsValid) {
        formDisabled=true
    }

    const inputChangeHandle = (e) => {
        setEnteredValue(e.target.value)
        setEnterNameTouched(true)
    }

    const inputBlurHandle = (e) => {
        setEnterNameTouched(true)
    }

    const onSubmitHAndler = (e) => {
        e.preventDefault()
        setEnterNameTouched(true)
        if(!enteredNameIsValid && !enteredEmailIsValid) {
            return
        }
        setEnteredValue('')
        setEnteredEmail('')
        setEnterNameTouched(false)


    }

const inputValue = nameInputFieldIsInvalid ? 'form-control invalid' : 'form-control'
  return (
    <form onSubmit={onSubmitHAndler}>
      <div className={inputValue}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name'
               value={enteredValue}
               onBlur={inputBlurHandle}
               onChange={inputChangeHandle}/>
          {nameInputFieldIsInvalid  && <p className="error-text">Name should not be empty</p>}
      </div>

        <div className={inputValue}>
            <label htmlFor='name'>Your Email</label>
            <input type='email'
                   id='email'
                   // value={enteredValue}
                   // onBlur={inputBlurHandle}
                   // onChange={inputChangeHandle}
            />
        </div>

      <div className="form-actions">
        <button disabled={!formDisabled} >Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
