import React,{useState} from "react";
const useBasicInputHook = (validate) => {
    const [enterValue,setEnterValue] = useState('')
    const [isTouched,setIsTouched] = useState(false)

    const valueIsValid = validate(enterValue)
    const hasError = !valueIsValid && isTouched

    const onChangeFirstNameHandler = (e) => {
        setEnterValue(e.target.value)
    }

    const FirstNameBlurHandler = () => {
        setIsTouched(true)
    }

    const reset = () => {
        setEnterValue('')
        setIsTouched(false)
    }
return {
        enterValue,isTouched,onChangeFirstNameHandler,FirstNameBlurHandler,reset,valueIsValid,hasError
}
}
export default useBasicInputHook
