import React,{useState,useRef} from "react";
import  useInputCustomHook from '../customHook/inputHook'
const SimpleInput = (props) => {
    // const [enteredValue,setEnteredValue] = useState('')
    // const [enterNameTouched, setEnterNameTouched] = useState(false)
   const { value: enteredName, hasError: nameInputHasError, valueChangeHandler:nameChaneHandler,
       inputBlurHandler:nameBlurHandler,isValid:enteredNameIsValid,reset:resetNameInput
   } = useInputCustomHook( value => value.trim()!== '')
    const [enteredEmail,setEnteredEmail] = useState('')
    const [emailEnterNameTouched, setEmailEnterNameTouched] = useState(false)

console.log('enteredNameIsValid',enteredNameIsValid)

//     const enteredNameIsValid = enteredValue.trim() !== ''
// const nameInputFieldIsInvalid = !enteredNameIsValid && enterNameTouched;

const enteredEmailIsValid = enteredEmail.includes('@')
const emailInputIsInvalid = !enteredEmailIsValid && emailEnterNameTouched


    let formDisabled = false
    // if(enteredNameIsValid && enteredEmailIsValid) {
    //     formDisabled=true
    // }

    if(enteredNameIsValid) {
        formDisabled=true
    }
    const inputChangeHandle = (e,type) => {
            setEnteredEmail(e.target.value)
            setEmailEnterNameTouched(true)
    }

    // const inputChangeHandle = (e,type) => {
    //     if(type === 'name')
    //     {
    //         setEnteredValue(e.target.value)
    //         setEnterNameTouched(true)
    //
    //     } else {
    //         setEnteredEmail(e.target.value)
    //         setEmailEnterNameTouched(true)
    //     }
    // }

    // const inputBlurHandle = (e,type) => {
    //     if(type === 'name') {
    //         setEnterNameTouched(true)
    //     } else {
    //         setEmailEnterNameTouched(true)
    //     }
    //
    // }
    const inputBlurHandle = (e,type) => {
            setEmailEnterNameTouched(true)

    }

    const onSubmitHAndler = (e) => {
        e.preventDefault()
        setEnterNameTouched(true)
        setEmailEnterNameTouched(true)
        if(!enteredNameIsValid && !enteredEmailIsValid) {
            return
        }
        resetNameInput()

        setEmailEnterNameTouched(false)
        setEnteredEmail('')



    }

const inputValue = nameInputHasError ? 'form-control invalid' : 'form-control'
const emailValue = emailInputIsInvalid ? 'form-control invalid' : 'form-control'
  return (
    <form onSubmit={onSubmitHAndler}>
      <div className={inputValue}>
          {console.log('formDisabled',formDisabled)}
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name'
               value={enteredName}
               onBlur={nameBlurHandler}
               onChange={nameChaneHandler}/>
          {nameInputHasError  && <p className="error-text">Name should not be empty</p>}
      </div>

        {/*<div className={emailValue}>*/}
        {/*    <label htmlFor='name'>Your Email</label>*/}
        {/*    <input type='email'*/}
        {/*           id='email'*/}
        {/*           onChange={(e) => {inputChangeHandle(e,'email')}}*/}
        {/*           value={enteredEmail}*/}
        {/*           onBlur={(e) => {inputBlurHandle(e,'email')}}*/}
        {/*    />*/}
        {/*    {emailInputIsInvalid  && <p className="error-text">Email should not be empty</p>}*/}

        {/*</div>*/}

      <div className="form-actions">
        <button disabled={!formDisabled} >Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
