import React,{useState} from "react";
const BasicForm = (props) => {
    const [enterFirstName,setEnterFirstName] = useState('')
    const [firstNameBlurHandler,setFirstNameBlurHandler] = useState(false)

    const [enterLastName,setEnterLastName] = useState('')
    const [lastNameBlurHandler,setLastNameBlurHandler] = useState(false)


    const [enterEmail,setEnterEmailName] = useState('')
    const [emailNameBlurHandler,setEmailNameBlurHandler] = useState(false)




    const firstNameIsValid = enterFirstName.trim()!==''
    const lastNameIsValid = enterLastName.trim()!==''
    const emailNameIsValid = enterEmail.includes('@')

    const FirstNameInputHasError = !firstNameIsValid && firstNameBlurHandler
    const LastNameInputHasError = !lastNameIsValid && lastNameBlurHandler
    const EmailNameInputHasError = !emailNameBlurHandler && emailNameIsValid

    let formIsValid = false
    if(firstNameIsValid) {
        formIsValid=true
    }
    console.log('formIsValid',formIsValid)

    const onChangeFirstNameHandler = (e) => {
        setEnterFirstName(e.target.value)
    }
    const onChangeLastNameHandler = (e) => {
        setEnterLastName(e.target.value)
    }
    const onChangeEmailHandler = (e) => {
        setEnterEmailName(e.target.value)
    }

    const FirstNameBlurHandler = () => {
        setFirstNameBlurHandler(true)
    }
    const LastNameBlurHandler = () => {
        setLastNameBlurHandler(true)
    }
    const EmailNameBlurHandler = () => {
        setEmailNameBlurHandler(true)
    }

    const submitHandler = (e) => {
        e.preventDefault()

        setEnterEmailName('')
        setEnterFirstName('')
        setEnterLastName('')


    }
console.log('LastNameInputHasError',LastNameInputHasError)
    const FirstInputValue = FirstNameInputHasError ? 'form-control invalid' : 'form-control'
    const LastInputValue = LastNameInputHasError ? 'form-control invalid' : 'form-control'
    const EmailInputValue = EmailNameInputHasError ? 'form-control invalid' : 'form-control'

    return (
    <form onSubmit={submitHandler}>
      <div className={FirstInputValue}>
        <div className='form-control'>
          <label htmlFor='name'>First Name</label>
          <input type='text' id='name' onChange={onChangeFirstNameHandler}
                 onBlur={FirstNameBlurHandler}
          value={enterFirstName}
          />
            {FirstNameInputHasError  && <p className="error-text">First name should not be empty</p>}
        </div>
        <div className={LastInputValue}>
          <label htmlFor='name'>Last Name</label>
          <input type='text' id='name' onChange={onChangeLastNameHandler}
                 onBlur={LastNameBlurHandler}
          value={enterLastName}
          />
            {LastNameInputHasError  && <p className="error-text">Last name should not be empty</p>}

        </div>
      </div>
      <div className={EmailInputValue}>
        <label htmlFor='name'>E-Mail Address</label>
        <input type='text' id='name'  onChange={onChangeEmailHandler}
               onBlur={EmailNameBlurHandler}
        value={enterEmail}
        />
          {EmailNameInputHasError  && <p className="error-text">Email should not be empty</p>}

      </div>
      <div className='form-actions'>
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default BasicForm;
